import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { AvatarComponent } from '../shared/components/avatar.component';

@Component({
  selector: 'app-chat',
  standalone: true,
  imports: [CommonModule, FormsModule, AvatarComponent],
  template: `
    <div class="chat-container">
      <!-- Contacts List -->
      <div class="contacts-panel">
        <div class="panel-header">
          <h3>Messages</h3>
          <button class="new-chat-btn">‚úèÔ∏è</button>
        </div>
        <div class="search-box">
          <input [(ngModel)]="searchQuery" placeholder="üîç Search contacts...">
        </div>
        <div class="contacts-list">
          <div *ngFor="let contact of filteredContacts" 
               class="contact-item"
               [class.active]="selectedContact?.id === contact.id"
               (click)="selectContact(contact)">
            <app-avatar [src]="contact.avatar" [online]="contact.online"></app-avatar>
            <div class="contact-info">
              <h4>{{contact.name}}</h4>
              <p class="last-message">{{contact.lastMessage}}</p>
            </div>
            <div class="contact-meta">
              <span class="time">{{contact.time}}</span>
              <span *ngIf="contact.unread" class="unread-badge">{{contact.unread}}</span>
            </div>
          </div>
        </div>
      </div>

      <!-- Chat Area -->
      <div class="chat-panel">
        <div *ngIf="!selectedContact" class="empty-chat">
          <h2>üí¨ Select a conversation</h2>
          <p>Choose a contact to start messaging</p>
        </div>

        <div *ngIf="selectedContact" class="chat-content">
          <!-- Chat Header -->
          <div class="chat-header">
            <div class="chat-user">
              <app-avatar [src]="selectedContact.avatar" [online]="selectedContact.online"></app-avatar>
              <div>
                <h3>{{selectedContact.name}}</h3>
                <span class="status">{{selectedContact.online ? 'Online' : 'Offline'}}</span>
              </div>
            </div>
            <button class="more-btn">‚ãÆ</button>
          </div>

          <!-- Messages -->
          <div class="messages-area">
            <div *ngFor="let message of messages" 
                 class="message"
                 [class.sent]="message.sent"
                 [class.received]="!message.sent">
              <div class="message-bubble">
                <p>{{message.content}}</p>
                <span class="message-time">{{message.time}}</span>
              </div>
            </div>
          </div>

          <!-- Message Input -->
          <div class="message-input">
            <button class="attach-btn">üìé</button>
            <input [(ngModel)]="newMessage" 
                   (keyup.enter)="sendMessage()"
                   placeholder="Type a message...">
            <button class="send-btn" (click)="sendMessage()">
              ‚úàÔ∏è
            </button>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: [`
    .chat-container {
      display: flex;
      height: calc(100vh - 100px);
      background: linear-gradient(135deg, rgba(240, 253, 250, 0.9), rgba(255, 255, 255, 0.95));
      border: 2px solid rgba(20, 184, 166, 0.2);
      border-radius: 16px;
      overflow: hidden;
      box-shadow: 0 4px 20px rgba(20, 184, 166, 0.1);
    }
    .contacts-panel {
      width: 350px;
      border-right: 2px solid rgba(20, 184, 166, 0.2);
      display: flex;
      flex-direction: column;
      background: white;
    }
    .panel-header {
      padding: 20px;
      border-bottom: 2px solid rgba(20, 184, 166, 0.2);
      display: flex;
      justify-content: space-between;
      align-items: center;
      background: linear-gradient(135deg, #14B8A6, #F97316);
      color: white;
    }
    .panel-header h3 {
      margin: 0;
      font-size: 20px;
    }
    .new-chat-btn {
      background: rgba(255, 255, 255, 0.2);
      border: none;
      border-radius: 50%;
      width: 40px;
      height: 40px;
      font-size: 20px;
      cursor: pointer;
      transition: all 0.3s ease;
    }
    .new-chat-btn:hover {
      background: rgba(255, 255, 255, 0.3);
      transform: scale(1.1);
    }
