import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-chat',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div style="padding: 2rem;">
      <h1>Chat Micro-Frontend</h1>
      <p>This is the chat module loaded from port 4203</p>
      <div style="margin-top: 2rem; border: 1px solid #ddd; padding: 1rem;">
        <h3>Chat Messages</h3>
        <p>Message 1: Hello!</p>
        <p>Message 2: How are you?</p>
      </div>
    </div>
  `
})
export class ChatComponent { }
