import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { HttpClientModule } from '@angular/common/http';
import { FeedService, Post } from '../services/feed.service';
import { PostCardComponent } from '../shared/components/post-card.component';

@Component({
  selector: 'app-feed',
  standalone: true,
  imports: [CommonModule, HttpClientModule, PostCardComponent],
  template: `
    <div class="feed-container">
      <div class="feed-header">
        <h2>Feed</h2>
        <div class="feed-filters">
          <button class="filter-btn" [class.active]="activeFeed === 'universal'" (click)="switchFeed('universal')">
            üåç Universal Feed
          </button>
          <button class="filter-btn" [class.active]="activeFeed === 'following'" (click)="switchFeed('following')">
            üë• Following Feed
          </button>
        </div>
      </div>

      <div *ngIf="loading" class="loading">
        <div class="spinner"></div>
        <p>Loading posts...</p>
      </div>

      <div *ngIf="error" class="error-message">
        {{ error }}
      </div>

      <div *ngIf="!loading && posts.length === 0" class="empty-state">
        <p>No posts available. Start following users to see their posts!</p>
      </div>

      <app-post-card 
        *ngFor="let post of posts" 
        [post]="post"
        (like)="likePost(post)"
        (share)="sharePost(post)"
        (comment)="addComment(post, $event)">
      </app-post-card>

      <button 
        *ngIf="!loading && posts.length > 0" 
        (click)="loadMore()" 
        class="load-more-btn">
        Load More
      </button>
    </div>
  `,
  styles: [`
    .feed-container {
      padding: 25px;
      background: linear-gradient(135deg, rgba(240, 253, 250, 0.3), rgba(255, 255, 255, 0.5));
      border-radius: 20px;
      min-height: 70vh;
    }
    .feed-header {
      margin-bottom: 30px;
    }
    .feed-header h2 {
      margin-bottom: 20px;
      color: #134E4A;
    }
    .feed-filters {
      display: flex;
      gap: 15px;
      justify-content: center;
    }
    .filter-btn {
      padding: 12px 24px;
      border: 2px solid rgba(20, 184, 166, 0.3);
      border-radius: 25px;
      background: white;
      color: #134E4A;
      font-weight: 600;
      cursor: pointer;
      transition: all 0.3s ease;
    }
    .filter-btn:hover {
      background: rgba(20, 184, 166, 0.1);
      transform: translateY(-2px);
    }
    .filter-btn.active {
      background: linear-gradient(135deg, #14B8A6, #F97316);
      color: white;
      border-color: transparent;
      box-shadow: 0 4px 15px rgba(20, 184, 166, 0.3);
    }
    .loading {
      text-align: center;
      padding: 3rem;
    }
    .spinner {
      border: 4px solid rgba(20, 184, 166, 0.2);
      border-top: 4px solid #14B8A6;
      border-radius: 50%;
      width: 50px;
      height: 50px;
      animation: spin 1s linear infinite;
      margin: 0 auto 20px;
    }
    @keyframes spin {
      0% { transform: rotate(0deg); }
      100% { transform: rotate(360deg); }
    }
    .error-message {
      color: #DC2626;
      padding: 1rem;
      background: #FEE2E2;
      border-radius: 8px;
      margin-bottom: 20px;
    }
    .empty-state {
      text-align: center;
      padding: 3rem;
      color: #666;
    }
    .load-more-btn {
      width: 100%;
      padding: 15px;
      background: linear-gradient(135deg, #14B8A6, #F97316);
      color: white;
      border: none;
      border-radius: 25px;
      font-weight: 600;
      cursor: pointer;
      transition: all 0.3s ease;
    }
    .load-more-btn:hover {
      transform: translateY(-2px);
      box-shadow: 0 6px 15px rgba(20, 184, 166, 0.3);
    }
  `]
})
export class FeedComponent implements OnInit {
  posts: Post[] = [];
  loading = false;
  error = '';
  currentPage = 0;
  activeFeed = 'universal';

  constructor(private feedService: FeedService) {}

  ngOnInit(): void {
    this.loadFeed();
  }

  switchFeed(feedType: string): void {
    this.activeFeed = feedType;
    this.currentPage = 0;
    this.posts = [];
    this.loadFeed();
  }

  loadFeed(): void {
    this.loading = true;
    this.error = '';

    this.feedService.getFeed(this.currentPage).subscribe({
      next: (response) => {
        this.posts = [...this.posts, ...response.posts];
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Failed to load feed. Please check if you are logged in.';
        this.loading = false;
        console.error('Feed error:', err);
      }
    });
  }

  loadMore(): void {
    this.currentPage++;
    this.loadFeed();
  }

  likePost(post: Post): void {
    console.log('Liked post:', post.id);
  }

  sharePost(post: Post): void {
    console.log('Shared post:', post.id);
  }

  addComment(post: Post, content: string): void {
    console.log('Added comment to post:', post.id, content);
  }

  formatDate(dateString: string): string {
    return new Date(dateString).toLocaleDateString();
  }
}
