import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-profile',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div style="padding: 2rem;">
      <h1>Profile Micro-Frontend</h1>
      <p>This is the profile module loaded from port 4202</p>
      <div style="margin-top: 2rem; border: 1px solid #ddd; padding: 1rem;">
        <h3>User Profile</h3>
        <p>Username: sample_user</p>
        <p>Email: user&#64;example.com</p>
      </div>
    </div>
  `
})
export class ProfileComponent { }
