: 2;\n  padding: 0 20px;\n}\n\n.new-avatar-wrapper[_ngcontent-%COMP%] {\n  flex-shrink: 0;\n}\n\n.new-profile-avatar[_ngcontent-%COMP%] {\n  width: 120px;\n  height: 120px;\n  border-radius: 50%;\n  border: 4px solid white;\n  object-fit: cover;\n  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.2);\n}\n\n.new-user-details[_ngcontent-%COMP%] {\n  flex: 1;\n  background: white;\n  padding: 20px;\n  border-radius: 15px;\n  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);\n}\n\n.new-username[_ngcontent-%COMP%] {\n  font-size: 2rem;\n  font-weight: 700;\n  margin: 0 0 10px 0;\n  color: #14B8A6;\n}\n\n.new-bio[_ngcontent-%COMP%] {\n  color: #666;\n  margin: 0 0 15px 0;\n  line-height: 1.5;\n}\n\n.new-user-stats[_ngcontent-%COMP%] {\n  display: flex;\n  gap: 30px;\n}\n\n.new-stat-item[_ngcontent-%COMP%] {\n  text-align: center;\n  cursor: pointer;\n}\n\n.new-stat-item[_ngcontent-%COMP%]   .new-count[_ngcontent-%COMP%] {\n  display: block;\n  font-size: 1.5rem;\n  font-weight: 700;\n  color: #14B8A6;\n}\n\n.new-stat-item[_ngcontent-%COMP%]   .new-label[_ngcontent-%COMP%] {\n  font-size: 0.9rem;\n  color: #666;\n  text-transform: uppercase;\n  letter-spacing: 1px;\n}\n\n.new-profile-actions[_ngcontent-%COMP%] {\n  align-self: flex-start;\n  margin-top: 20px;\n  display: flex;\n  flex-direction: column;\n  gap: 10px;\n}\n\n.new-profile-stats-card[_ngcontent-%COMP%] {\n  background: white;\n  border-radius: 15px;\n  padding: 25px;\n  margin-bottom: 30px;\n  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);\n}\n\n.new-stats-grid[_ngcontent-%COMP%] {\n  display: grid;\n  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));\n  gap: 20px;\n  margin-bottom: 30px;\n}\n\n.new-stat-box[_ngcontent-%COMP%] {\n  display: flex;\n  align-items: center;\n  gap: 15px;\n  padding: 20px;\n  background: linear-gradient(135deg, #f0fdfa, #ccfbf1);\n  border-radius: 12px;\n  border-left: 4px solid #14B8A6;\n}\n\n.new-stat-icon[_ngcontent-%COMP%] {\n  font-size: 2rem;\n  color: #14B8A6;\n}\n\n.new-stat-info[_ngcontent-%COMP%] {\n  display: flex;\n  flex-direction: column;\n}\n\n.new-stat-number[_ngcontent-%COMP%] {\n  font-size: 1.8rem;\n  font-weight: 700;\n  color: #14B8A6;\n}\n\n.new-stat-text[_ngcontent-%COMP%] {\n  font-size: 0.9rem;\n  color: #666;\n  text-transform: uppercase;\n  letter-spacing: 1px;\n}\n\n.new-recent-activity[_ngcontent-%COMP%]   h4[_ngcontent-%COMP%] {\n  color: #14B8A6;\n  margin-bottom: 15px;\n  display: flex;\n  align-items: center;\n  gap: 10px;\n}\n\n.new-activity-list[_ngcontent-%COMP%] {\n  max-height: 200px;\n  overflow-y: auto;\n}\n\n.new-activity-item[_ngcontent-%COMP%] {\n  display: flex;\n  align-items: center;\n  gap: 15px;\n  padding: 10px 0;\n  border-bottom: 1px solid #f0f0f0;\n}\n\n.new-activity-item[_ngcontent-%COMP%]:last-child {\n  border-bottom: none;\n}\n\n.new-activity-item[_ngcontent-%COMP%]   i[_ngcontent-%COMP%] {\n  color: #14B8A6;\n  width: 20px;\n}\n\n.new-activity-item[_ngcontent-%COMP%]   small[_ngcontent-%COMP%] {\n  margin-left: auto;\n  color: #999;\n}\n\n.new-tab-navigation[_ngcontent-%COMP%] {\n  display: flex;\n  background: white;\n  border-radius: 15px;\n  padding: 5px;\n  margin-bottom: 30px;\n  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);\n}\n\n.new-tab-btn[_ngcontent-%COMP%] {\n  flex: 1;\n  padding: 15px 20px;\n  border: none;\n  background: transparent;\n  border-radius: 10px;\n  font-weight: 600;\n  color: #666;\n  transition: all 0.3s ease;\n  cursor: pointer;\n}\n\n.new-tab-btn[_ngcontent-%COMP%]:hover {\n  background: #f0fdfa;\n  color: #14B8A6;\n}\n\n.new-tab-btn.active[_ngcontent-%COMP%] {\n  background: linear-gradient(135deg, #14B8A6, #5EEAD4);\n  color: white;\n  box-shadow: 0 4px 15px rgba(20, 184, 166, 0.3);\n}\n\n.new-tab-btn[_ngcontent-%COMP%]   i[_ngcontent-%COMP%] {\n  margin-right: 8px;\n}\n\n.new-tab-content[_ngcontent-%COMP%] {\n  min-height: 400px;\n}\n\n.new-posts-grid[_ngcontent-%COMP%] {\n  display: grid;\n  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));\n  gap: 20px;\n}\n\n.new-post-card[_ngcontent-%COMP%] {\n  background: white;\n  border-radius: 15px;\n  overflow: hidden;\n  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);\n  transition: transform 0.3s ease;\n}\n\n.new-post-card[_ngcontent-%COMP%]:hover {\n  transform: translateY(-5px);\n}\n\n.new-post-image[_ngcontent-%COMP%] {\n  position: relative;\n  height: 200px;\n  overflow: hidden;\n}\n\n.new-post-image[_ngcontent-%COMP%]   img[_ngcontent-%COMP%], .new-post-image[_ngcontent-%COMP%]   video[_ngcontent-%COMP%] {\n  width: 100%;\n  height: 100%;\n  object-fit: cover;\n}\n\n.new-post-overlay[_ngcontent-%COMP%] {\n  position: absolute;\n  top: 0;\n  left: 0;\n  right: 0;\n  bottom: 0;\n  background: rgba(0, 0, 0, 0.5);\n  display: flex;\n  align-items: center;\n  justify-content: center;\n  gap: 20px;\n  opacity: 0;\n  transition: opacity 0.3s ease;\n  color: white;\n  font-weight: 600;\n}\n\n.new-post-image[_ngcontent-%COMP%]:hover   .new-post-overlay[_ngcontent-%COMP%] {\n  opacity: 1;\n}\n\n.new-post-content[_ngcontent-%COMP%] {\n  padding: 20px;\n}\n\n.new-post-content[_ngcontent-%COMP%]   p[_ngcontent-%COMP%] {\n  margin: 0 0 15px 0;\n  line-height: 1.5;\n}\n\n.new-post-actions[_ngcontent-%COMP%] {\n  display: flex;\n  gap: 15px;\n}\n\n.new-btn-like[_ngcontent-%COMP%], .new-btn-comment[_ngcontent-%COMP%] {\n  background: none;\n  border: 1px solid #ddd;\n  padding: 8px 15px;\n  border-radius: 20px;\n  cursor: pointer;\n  transition: all 0.3s ease;\n  display: flex;\n  align-items: center;\n  gap: 5px;\n  font-size: 0.9rem;\n}\n\n.new-btn-like[_ngcontent-%COMP%]:hover, .new-btn-comment[_ngcontent-%COMP%]:hover {\n  border-color: #14B8A6;\n  color: #14B8A6;\n}\n\n.new-btn-like.liked[_ngcontent-%COMP%] {\n  background: #F97316;\n  color: white;\n  border-color: #F97316;\n}\n\n.new-empty-state[_ngcontent-%COMP%] {\n  text-align: center;\n  padding: 60px 20px;\n  color: #666;\n  background: white;\n  border-radius: 15px;\n  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);\n}\n\n.new-empty-state[_ngcontent-%COMP%]   i[_ngcontent-%COMP%] {\n  font-size: 4rem;\n  margin-bottom: 20px;\n  color: #ddd;\n}\n\n.new-about-section[_ngcontent-%COMP%] {\n  display: grid;\n  grid-template-columns: repeat(auto-fit, minmax(400px, 1fr));\n  gap: 30px;\n}\n\n.new-info-card[_ngcontent-%COMP%] {\n  background: white;\n  padding: 25px;\n  border-radius: 15px;\n  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);\n}\n\n.new-info-card[_ngcontent-%COMP%]   h3[_ngcontent-%COMP%] {\n  color: #14B8A6;\n  margin-bottom: 20px;\n  display: flex;\n  align-items: center;\n  gap: 10px;\n}\n\n.new-info-item[_ngcontent-%COMP%] {\n  display: flex;\n  justify-content: space-between;\n  padding: 10px 0;\n  border-bottom: 1px solid #f0f0f0;\n}\n\n.new-info-item[_ngcontent-%COMP%]:last-child {\n  border-bottom: none;\n}\n\n.new-info-item[_ngcontent-%COMP%]   label[_ngcontent-%COMP%] {\n  font-weight: 600;\n  color: #333;\n}\n\n.new-interests-grid[_ngcontent-%COMP%] {\n  display: flex;\n  flex-wrap: wrap;\n  gap: 10px;\n}\n\n.new-interest-tag[_ngcontent-%COMP%] {\n  background: linear-gradient(135deg, #14B8A6, #F97316);\n  color: white;\n  padding: 8px 15px;\n  border-radius: 20px;\n  font-size: 0.9rem;\n  font-weight: 500;\n}\n\n.new-followers-section[_ngcontent-%COMP%], .new-following-section[_ngcontent-%COMP%] {\n  background: white;\n  border-radius: 15px;\n  padding: 25px;\n  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);\n}\n\n.new-user-list[_ngcontent-%COMP%] {\n  display: grid;\n  gap: 15px;\n}\n\n.new-user-item[_ngcontent-%COMP%] {\n  display: flex;\n  align-items: center;\n  gap: 15px;\n  padding: 15px;\n  background: #f0fdfa;\n  border-radius: 12px;\n  transition: transform 0.3s ease;\n}\n\n.new-user-item[_ngcontent-%COMP%]:hover {\n  transform: translateX(5px);\n}\n\n.new-user-avatar[_ngcontent-%COMP%] {\n  width: 50px;\n  height: 50px;\n  border-radius: 50%;\n  object-fit: cover;\n}\n\n.new-user-info[_ngcontent-%COMP%] {\n  flex: 1;\n}\n\n.new-user-info[_ngcontent-%COMP%]   h4[_ngcontent-%COMP%] {\n  margin: 0 0 5px 0;\n  color: #14B8A6;\n}\n\n.new-user-info[_ngcontent-%COMP%]   p[_ngcontent-%COMP%] {\n  margin: 0;\n  color: #666;\n  font-size: 0.9rem;\n}\n\n@media (max-width: 768px) {\n  .new-profile-info-section[_ngcontent-%COMP%] {\n    flex-direction: column;\n    align-items: center;\n    text-align: center;\n    margin-top: -40px;\n  }\n  \n  .new-user-details[_ngcontent-%COMP%] {\n    width: 100%;\n  }\n  \n  .new-user-stats[_ngcontent-%COMP%] {\n    justify-content: center;\n  }\n  \n  .new-stats-grid[_ngcontent-%COMP%] {\n    grid-template-columns: 1fr;\n  }\n  \n  .new-tab-navigation[_ngcontent-%COMP%] {\n    flex-wrap: wrap;\n  }\n  \n  .new-tab-btn[_ngcontent-%COMP%] {\n    min-width: 120px;\n  }\n  \n  .new-posts-grid[_ngcontent-%COMP%] {\n    grid-template-columns: 1fr;\n  }\n  \n  .new-about-section[_ngcontent-%COMP%] {\n    grid-template-columns: 1fr;\n  }\n}\n/*# sourceMappingURL=data:application/json;charset=utf-8;base64, */"]
