: 20px;\n  outline: none;\n}\n\n.add-comment[_ngcontent-%COMP%]   button[_ngcontent-%COMP%] {\n  background: #14B8A6;\n  color: white;\n  border: none;\n  border-radius: 50%;\n  width: 40px;\n  height: 40px;\n  cursor: pointer;\n}\n\n\n\n.empty-state[_ngcontent-%COMP%] {\n  text-align: center;\n  padding: 60px 20px;\n  color: #666;\n  background: white;\n  border-radius: 15px;\n  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);\n}\n\n.empty-state[_ngcontent-%COMP%]   i[_ngcontent-%COMP%] {\n  font-size: 4rem;\n  margin-bottom: 20px;\n  color: #ddd;\n}\n\n\n\n.about-section[_ngcontent-%COMP%] {\n  display: grid;\n  grid-template-columns: repeat(auto-fit, minmax(400px, 1fr));\n  gap: 30px;\n}\n\n.info-card[_ngcontent-%COMP%] {\n  background: white;\n  padding: 25px;\n  border-radius: 15px;\n  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);\n}\n\n.info-card[_ngcontent-%COMP%]   h3[_ngcontent-%COMP%] {\n  color: #14B8A6;\n  margin-bottom: 20px;\n  display: flex;\n  align-items: center;\n  gap: 10px;\n}\n\n.info-item[_ngcontent-%COMP%] {\n  display: flex;\n  justify-content: space-between;\n  padding: 10px 0;\n  border-bottom: 1px solid #f0f0f0;\n}\n\n.info-item[_ngcontent-%COMP%]:last-child {\n  border-bottom: none;\n}\n\n.info-item[_ngcontent-%COMP%]   label[_ngcontent-%COMP%] {\n  font-weight: 600;\n  color: #333;\n}\n\n.interests-grid[_ngcontent-%COMP%] {\n  display: flex;\n  flex-wrap: wrap;\n  gap: 10px;\n}\n\n.interest-tag[_ngcontent-%COMP%] {\n  background: linear-gradient(135deg, #14B8A6, #F97316);\n  color: white;\n  padding: 8px 15px;\n  border-radius: 20px;\n  font-size: 0.9rem;\n  font-weight: 500;\n}\n\n\n\n.followers-section[_ngcontent-%COMP%], .following-section[_ngcontent-%COMP%] {\n  background: white;\n  border-radius: 15px;\n  padding: 25px;\n  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);\n}\n\n.user-list[_ngcontent-%COMP%] {\n  display: grid;\n  gap: 15px;\n}\n\n.user-item[_ngcontent-%COMP%] {\n  display: flex;\n  align-items: center;\n  gap: 15px;\n  padding: 15px;\n  background: #f0fdfa;\n  border-radius: 12px;\n  transition: transform 0.3s ease;\n}\n\n.user-item[_ngcontent-%COMP%]:hover {\n  transform: translateX(5px);\n}\n\n.user-avatar[_ngcontent-%COMP%] {\n  width: 50px;\n  height: 50px;\n  border-radius: 50%;\n  object-fit: cover;\n}\n\n.user-info[_ngcontent-%COMP%] {\n  flex: 1;\n}\n\n.user-info[_ngcontent-%COMP%]   h4[_ngcontent-%COMP%] {\n  margin: 0 0 5px 0;\n  color: #14B8A6;\n}\n\n.user-info[_ngcontent-%COMP%]   p[_ngcontent-%COMP%] {\n  margin: 0;\n  color: #666;\n  font-size: 0.9rem;\n}\n\n\n\n@media (max-width: 768px) {\n  .profile-info-section[_ngcontent-%COMP%] {\n    flex-direction: column;\n    align-items: center;\n    text-align: center;\n    margin-top: -40px;\n  }\n  \n  .user-details[_ngcontent-%COMP%] {\n    width: 100%;\n  }\n  \n  .user-stats[_ngcontent-%COMP%] {\n    justify-content: center;\n  }\n  \n  .stats-grid[_ngcontent-%COMP%] {\n    grid-template-columns: 1fr;\n  }\n  \n  .tab-navigation[_ngcontent-%COMP%] {\n    flex-wrap: wrap;\n  }\n  \n  .tab-btn[_ngcontent-%COMP%] {\n    min-width: 120px;\n  }\n  \n  .posts-grid[_ngcontent-%COMP%] {\n    grid-template-columns: 1fr;\n  }\n  \n  .about-section[_ngcontent-%COMP%] {\n    grid-template-columns: 1fr;\n  }\n}\n/*# sourceMappingURL=data:application/json;charset=utf-8;base64, */"]
