import { Component, OnInit } from '@angular/core';
import { RouterModule, Router } from '@angular/router';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { AuthService, LoginRequest } from '../../../core/services/auth.service';

@Component({
  selector: 'app-login',
  standalone: true,
  imports: [RouterModule, CommonModule, FormsModule],
  templateUrl: './login.component.html',
  styleUrl: './login.component.css'
})
export class LoginComponent implements OnInit {
  displayText = '';
  fullText = 'RevHub';
  showForm = false;
  
  loginData: LoginRequest = {
    username: '',
    password: ''
  };
  
  isLoading = false;
  errorMessage = '';
  
  constructor(
    private authService: AuthService,
    private router: Router
  ) {}
  
  ngOnInit() {
    this.typeText();
  }
  

  
  typeText() {
    let i = 0;
    const interval = setInterval(() => {
      this.displayText = this.fullText.substring(0, i + 1);
      i++;
      if (i >= this.fullText.length) {
        clearInterval(interval);
        setTimeout(() => {
          this.showForm = true;
        }, 500);
      }
    }, 300);
  }
  
  onSubmit() {
    if (this.loginData.username && this.loginData.password) {
      this.isLoading = true;
      this.errorMessage = '';
      
      this.authService.login(this.loginData).subscribe({
        next: (response) => {
          this.isLoading = false;
          this.router.navigate(['/dashboard']);
        },
        error: (error) => {
          this.isLoading = false;
          this.errorMessage = 'Invalid credentials. Please try again.';
        }
      });
    }
  }
}
<div class="auth-container">
  <div class="floating-shapes">
    <div class="shape"></div>
    <div class="shape"></div>
    <div class="shape"></div>
  </div>
  
  <div class="auth-card">
    <div class="text-center mb-4">
      <h1 class="revhub-title">RevHub</h1>
      <p class="subtitle">Connect, Share, Discover</p>
    </div>
    
    <form (ngSubmit)="onSubmit()">
      <div class="form-floating mb-3">
        <input 
          type="text" 
          class="form-control" 
          id="username"
          placeholder="Username" 
          [(ngModel)]="loginData.username"
          name="username"
          required>
        <label for="username">
          <i class="fas fa-user me-2"></i>Username
        </label>
      </div>
      
      <div class="form-floating mb-4">
        <input 
          type="password" 
          class="form-control" 
          id="password"
          placeholder="Password" 
          [(ngModel)]="loginData.password"
          name="password"
          required>
        <label for="password">
          <i class="fas fa-lock me-2"></i>Password
        </label>
      </div>
      
      <div class="mb-3" *ngIf="errorMessage">
        <div class="alert alert-danger">
          <i class="fas fa-exclamation-triangle me-2"></i>{{errorMessage}}
        </div>
      </div>
      
      <button 
        type="submit" 
        class="btn btn-primary w-100 py-3" 
        [disabled]="isLoading || !loginData.username || !loginData.password">
        <span *ngIf="isLoading" class="spinner-border spinner-border-sm me-2"></span>
        <i *ngIf="!isLoading" class="fas fa-sign-in-alt me-2"></i>
        {{isLoading ? 'Signing in...' : 'Sign In'}}
      </button>
    </form>
    
    <div class="text-center mt-4">
      <div class="mb-2">
        <a routerLink="/auth/register" class="text-decoration-none" style="color: #667eea;">
          <i class="fas fa-user-plus me-1"></i>Don't have an account? Sign up
        </a>
      </div>
      <div>
        <a routerLink="/auth/forgot-password" class="text-muted text-decoration-none">
          <i class="fas fa-key me-1"></i>Forgot Password?
        </a>
      </div>
    </div>
  </div>
</div>
