import { Component } from '@angular/core';
import { RouterModule, Router } from '@angular/router';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { AuthService, RegisterRequest } from '../../../core/services/auth.service';

@Component({
  selector: 'app-register',
  standalone: true,
  imports: [RouterModule, CommonModule, FormsModule],
  templateUrl: './register.component.html',
  styleUrl: './register.component.css'
})
export class RegisterComponent {
  registerData: RegisterRequest = {
    username: '',
    email: '',
    password: ''
  };
  
  isLoading = false;
  errorMessage = '';
  successMessage = '';
  
  constructor(
    private authService: AuthService,
    private router: Router
  ) {}
  
  onSubmit() {
    if (this.registerData.username && this.registerData.email && this.registerData.password) {
      this.isLoading = true;
      this.errorMessage = '';
      this.successMessage = '';
      
      this.authService.register(this.registerData).subscribe({
        next: (response) => {
          this.isLoading = false;
          this.successMessage = 'Registration successful! Please check your email for OTP.';
          setTimeout(() => {
            this.router.navigate(['/auth/verify-otp'], { queryParams: { email: this.registerData.email } });
          }, 2000);
        },
        error: (error) => {
          this.isLoading = false;
          
          if (error.error && typeof error.error === 'string') {
            this.errorMessage = error.error;
          } else if (error.status === 0) {
            this.errorMessage = 'Cannot connect to server. Please check if backend is running.';
          } else if (error.status === 400) {
            this.errorMessage = error.error || 'Invalid registration data. Please check your inputs.';
          } else {
            this.errorMessage = `Registration failed (${error.status}): ${error.error || error.message || 'Please try again.'}`;
          }
        }
      });
    }
  }
}
<div class="auth-container">
  <div class="floating-shapes">
    <div class="shape"></div>
    <div class="shape"></div>
    <div class="shape"></div>
  </div>
  
  <div class="auth-card">
    <div class="text-center mb-4">
      <h1 class="revhub-title">RevHub</h1>
      <p class="subtitle">Join the Community</p>
    </div>
        <form (ngSubmit)="onSubmit()">
          <div class="form-floating mb-3">
            <input 
              type="text" 
              class="form-control" 
              id="username"
              placeholder="Username" 
              [(ngModel)]="registerData.username"
              name="username"
              required>
            <label for="username">
              <i class="fas fa-user me-2"></i>Username
            </label>
          </div>
          
          <div class="form-floating mb-3">
            <input 
              type="email" 
              class="form-control" 
              id="email"
              placeholder="Email" 
              [(ngModel)]="registerData.email"
              name="email"
              required>
            <label for="email">
              <i class="fas fa-envelope me-2"></i>Email
            </label>
          </div>
          
          <div class="form-floating mb-4">
            <input 
              type="password" 
              class="form-control" 
              id="password"
              placeholder="Password" 
              [(ngModel)]="registerData.password"
              name="password"
              required>
            <label for="password">
              <i class="fas fa-lock me-2"></i>Password
            </label>
          </div>
          
          <div class="mb-3" *ngIf="errorMessage">
            <div class="alert alert-danger">
              <i class="fas fa-exclamation-triangle me-2"></i>{{errorMessage}}
            </div>
          </div>
          
          <div class="mb-3" *ngIf="successMessage">
            <div class="alert alert-success">
              <i class="fas fa-check-circle me-2"></i>{{successMessage}}
            </div>
          </div>
          
          <button 
            type="submit" 
            class="btn btn-primary w-100 py-3" 
            [disabled]="isLoading || !registerData.username || !registerData.email || !registerData.password">
            <span *ngIf="isLoading" class="spinner-border spinner-border-sm me-2"></span>
            <i *ngIf="!isLoading" class="fas fa-user-plus me-2"></i>
            {{isLoading ? 'Creating Account...' : 'Create Account'}}
          </button>
        </form>
        
        <div class="text-center mt-4">
          <a routerLink="/auth/login" class="text-decoration-none" style="color: #14B8A6;">
            <i class="fas fa-sign-in-alt me-1"></i>Already have an account? Sign in
          </a>
        </div>
    </div>
</div>
